/* #omni */
#vomnibar {
background-color: #181C14;
border: none;
}

#vomnibar .vomnibarSearchArea{
  position: relative;

background-color: #181C14;

border: none;
}

#vomnibar .vomnibarSearchArea::after {
  content: '';
  position: absolute;
  bottom: 0;

  left: 50%;
  transform: translateX(-50%);

  width: 95%;
  border-bottom: 1px solid #3C3D37;
}

#vomnibar .vomnibarSearchArea input{

outline: none;
border: none;
box-shadow: none;

color: #EEEEEE;
background-color: #3C3D37;
}


#vomnibar ul{
padding: 10px;
margin: 0;

background-color: #181C14;
}

#vomnibar ul li:first-child {
border-radius: 4px 4px 0 0;
overflow: hidden;

padding-top: 20px
display: none;
pointer-events: none;
    user-select: none;
    outline: none;
}

#vomnibar ul li:last-child {
border-radius: 0 0 4px 4px;
overflow: hidden;
}

#vomnibar li {
display: flex;
flex-flow: row nowrap;
gap: 10px;
border: none;

transition: background-color 0.25s ease;
}

#vomnibar li .vomnibarTopHalf {
display: flex;
justify-content: center;
align-items: center;
margin: 0;
padding: 0;
order: 2;
}

#vomnibar li .vomnibarBottomHalf {
display: flex;
justify-content: center;
align-items: center;
margin: 0;
padding: 0;
order: 1;
}

#vomnibar li.vomnibarBottomHalf img {
width: 100%;
height: 100%;

display: block;
  box-shadow: 0 0 5px 10px #3C3D37;
}

#vomnibar li.vomnibarSelected {

    background-color: #31363F;
    background-color: #525252;
}

#vomnibar li:not(.vomnibarSelected){
background-color: #3C3D37;
}

#vomnibar li .vomnibarTitle {
display: inline-block;

line-height: 100%;
height: 100%;

color: #EEEEEE;
font-size: 1.2rem;

}

#vomnibar .vomnibarSource, 
#vomnibar .vomnibarUrl {
display: none;
}
